mapper.insert:
insert into pay_trans_base(
      unique_id
      , trans_dv
      , card_no
      , instm_month
      , exp_dt
      , cvc
      , trans_amt
      , val_add_tax
      , org_unique_id
      , string_data
      , ctt
      , reg_dtm
      , reg_usr
 )
values(
    #{unique_id} 
      ,  #{trans_dv}
      ,  #{card_no}
      ,  #{instm_month}
      ,  #{exp_dt}
      ,  #{cvc}
      ,  #{trans_amt}
      ,  #{val_add_tax}
      ,  #{org_unique_id}
      ,  #{string_data}
      ,  #{ctt}
      ,  now()
      ,  #{reg_usr}
    )

