      , pay_amt
      , pay_val_add_tax              
      , ctt
      , reg_dtm
      , reg_usr
 )
values(
    #{success_yn} 
      ,  #{trans_dv}
      ,  #{unique_id}
      ,  #{org_unique_id}
      ,  #{status}
      ,  #{trans_amt}
      ,  #{val_add_tax}
      ,  #{pay_amt}
      ,  #{pay_val_add_tax}              
      ,  #{ctt}
      ,  now()
      ,  #{reg_usr}
    )
